/*
	 * 变量配置
	 */
	:root {
	    --color50: var(--teal50);
	    --color100: var(--teal100);
	    --color200: var(--teal200);
	    --color300: var(--teal300);
	    --color400: var(--teal400);
	    --color500: var(--teal500);
	    --color600: var(--teal600);
	    --color700: var(--teal700);
	    --color800: var(--teal800);
	    --color900: var(--teal900);
	    --colorA100: var(--tealA100);
	    --colorA200: var(--tealA200);
	    --colorA400: var(--tealA400);
	    --colorA700: var(--tealA700);
	
	    --color-accent: var(--color500);
	}

/*
	 * 板块（帖子列表）
	 * postList.css
	 */
	/* region logo*/
	#nav-logo > span{
	    opacity: 0;
	}
	
	#nav-logo {
	    position: relative;
	    top: -78px;
	    left:-50px;
	    z-index: 22;
	    width: 40px;
	    height: 40px; background:url(data:image/png;base64,);
	    background-position: center;
	    background-repeat: no-repeat;
	}
	/* endregion logo  */
	
	/*region 导航栏背景*/
	#nav-menu {
	    background: var(--color500);
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	}
	
	/*endregion*/
	
	/* region 加大导航栏宽度*/
	#nav-menu > ul > li {
	    width: unset;
	    margin-right: unset;
	}
	
	#nav-menu > ul > li > a {
	    padding: 20px 20px;
	}
	
	#nav-menu > ul > li.has-sub > a {
	    padding-right: 20px;
	}
	
	#nav-menu > ul > li.has-sub > a:after {
	    display: none;
	}
	
	/*endregion*/
	
	/*region 导航栏下拉菜单*/
	#nav-menu > ul > li:after {
	    background: var(--color300);
	}
	
	#nav-menu > ul > li > ul {
	    padding: 8px 0;
	    background: var(--color500);
	    border-radius: 0 0 4px 4px;
	    -webkit-box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 3px -2px rgba(0, 0, 0, .2), 0 3px 4px 0 rgba(0, 0, 0, .14), 0 1px 8px 0 rgba(0, 0, 0, .12)
	}
	
	#nav-menu > ul > li.has-sub > ul > li {
	    background: var(--color500);
	}
	
	#nav-menu > ul > li.has-sub > ul > li:hover {
	    background: var(--color300);
	}
	
	#nav-menu ul li ul li a {
	    color: #333333;
	    text-align: left;
	    padding: 15px 20px;
	}
	
	#nav-menu > ul > li > ul,
	#nav-menu ul li ul li a,
	#nav-menu > ul > li > ul > li {
	    min-width: 110px;
	}
	
	#nav-menu ul li ul li {
	    border: unset;
	}
	
	/*endregion*/
	
	/*region 搜索框*/
	.search-bar {
	    margin-top: 6px;
	    padding-left: 15px;
	    height: 30px;
	    border-radius: 20px;
	    border: 1px solid var(--color300);
	    transition: all .2s;
	}
	
	.search-bar:hover {
	    background: var(--color300);
	    /*-webkit-box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12);*/
	    /*box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12)*/
	}
	
	.search-bar .search-bar-form,
	.search-bar .search-box,
	.search-bar .dropdown .btn {
	    background: transparent;
	}
	
	.search-bar input::-webkit-input-placeholder {
	    color: var(--white1);
	}
	
	.search-bar > img:first-child {
	    display: none;
	}
	
	.search-bar button[type=submit] {
	    display: none;
	}
	
	.search-bar .dropdown .btn {
	    color: var(--white1);
	    border: none;
	    outline: medium;
	}
	
	.search-bar .search-bar-form {
	    position: relative;
	}
	
	.search-bar .search-box {
	    outline: medium;
	    border: none;
	}
	
	/*endregion*/
	
	/*region 导航栏和头像搜索框合并一行*/
	#nav-menu.align-center > ul {
	    margin-left: -466px;
	}
	
	.tb-container {
	    height: 0;
	    margin: 0 auto;
	}
	
	#nav-additional {
	    margin-top: -50px;
	    text-align: right;
	}
	
	#nav-search-bar,
	#nav-user-action-bar {
	    float: unset !important;
	    display: inline-block;
	    position: relative;
	    z-index: 20;
	}
	
	#nav-search-bar {
	    left: -17px;
	}
	
	#nav-user-action-bar {
	    top: -2px;
	}
	
	/*endregion*/
	
	/* region 有消息时的消息*/
	#nav-user-action-bar *,
	.btn-user-action * {
	    animation: unset !important;
	}
	
	#nav-additional .badge {
	    margin: unset !important;
	    background-color: transparent !important;
	    padding: 0 0 0 5px;
	    top: -2px !important;
	}
	
	#nav-user-action-bar .btn.btn-user-action {
	    transition: all .2s;
	    border-radius: 2px;
	    color: var(--white1);
	}
	
	#nav-user-action-bar .btn.btn-user-action:hover {
	    background-color: var(--color300);
	}
	
	/* endregion*/
	
	/* region 头像弹出菜单*/
	#nav-user-action-bar .dropdown-menu.dropdown-menu-right {
	    border-radius: 3px;
	    background: var(--color500);
	    border: 0;
	    -webkit-box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
	}
	
	#nav-user-action-bar .divider {
	    margin: 5px 0;
	    background: var(--color700);
	}
	
	#nav-user-action-bar .dropdown-menu.dropdown-menu-right a {
	    text-align: left;
	    font-size: 13px;
	    padding: 10px 20px;
	}
	
	.dropdown-menu > li > a:hover,
	.dropdown-menu > li > a:focus {
	    background: var(--color300);
	}
	
	/* endregion*/
	
	/* region 滚动到顶部*/
	#J_ScrollTopBtn {
	    border-radius: 50%;
	    background-color: var(--color500) !important;
	    -webkit-box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
	    box-shadow: 0 3px 5px -1px rgba(0, 0, 0, .2), 0 5px 8px 0 rgba(0, 0, 0, .14), 0 1px 14px 0 rgba(0, 0, 0, .12);
	
	    transition: all .2s ease-in-out;
	}
	
	#J_ScrollTopBtn:active {
	    -webkit-box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	}
	
	#J_ScrollTopBtn{
	    background-image: none !important;
	}
	#J_ScrollTopBtn:before {
	    content: '';
	    display: inline-block;
	    width: 32px;
	    height: 32px;
	    position: absolute;
	    top: 13px;
	    right: 6px;
	    background-size: 129%;
	    background-repeat: no-repeat;
	    background-position-x: -10px;
	    background-image: url(data:image/png;base64,);
	}
	
	/* endregion*/

/*
	 * 首页
	 * home.css
	 */
	/* region 主题色的背景条*/
	body:before {
	    content: '';
	    display: inline-block;
	    width: 100%;
	    min-width: 1200px;
	    height: 500px;
	    background: var(--color500);
	    position: absolute;
	    top: 0;
	    left: 0;
	}
	
	/* endregion*/
	
	/* region 整体 */
	.wp,
	#wp {
	    min-width: 100% !important;
	    max-width: 100% !important;
	    width: 100% !important;
	}
	
	.subject_row_detail,
	.subject_row_detail2 {
	    width: 17%;
	    padding: 16px;
	    border-radius: 3px;
	}
	
	#wp > div {
	    width: 100%;
	    min-width: 1200px;
	    height: unset !important;
	    margin: 0;
	    padding-top: 50px;
	    padding-bottom: 50px;
	}
	
	.subject_row_detail_text_down {
	    color: #333333 !important;
	}
	
	/* 背景色 */
	#diy1 {
	    width: 1200px;
	    margin: 0 auto;
	}
	
	#wp > div:nth-child(even) {
	    background: #f0f0f0;
	}
	
	#wp > div:nth-child(odd) {
	    background: #f7f7f7;
	}
	
	#wp > div:nth-child(2) {
	    background: #f7f7f7;
	}
	
