	#post_extra_tb > label:nth-child(1) {
	    border-radius: 0 0 0 5px;
	}
	
	#post_extra_tb label span {
	    background: unset;
	    padding: 0 8px;
	}
	
	/* endregion tab头 */
	
	/* region tab身子 */
	#post_extra_c > div {
	    background: var(--tab-bg-avtive);
	    border-radius: 0 0 5px 5px;
	    padding: 16px;
	}
	
	/* endregion tab身子 */
	
	
	/* endregion 附加选项、阅读权限、回帖奖励、抢楼主题、主题标签、定时发布 */
	
	.mtm.mbm.pnpost {
	    margin-top: 20px !important;
	}
	
	/* region 发表帖子、保存草稿 */
	#postsubmit {
	    color: var(--white1) !important;
	    background: var(--btn-bg) !important;
	}
	
	#postsubmit {
	    position: relative;
	    display: inline-block;
	    min-width: 88px;
	    height: 36px;
	    -webkit-box-sizing: border-box;
	    box-sizing: border-box;
	    padding: 0 16px;
	    margin: 0;
	    overflow: hidden;
	    font-size: 14px;
	    font-weight: 500;
	    line-height: 36px;
	    color: inherit;
	    text-align: center;
	    text-decoration: none;
	    text-transform: uppercase;
	    letter-spacing: .04em;
	    white-space: nowrap;
	    vertical-align: middle;
	    -ms-touch-action: manipulation;
	    touch-action: manipulation;
	    cursor: pointer;
	    zoom: 1;
	    -webkit-user-select: none;
	    -moz-user-select: none;
	    -ms-user-select: none;
	    user-select: none;
	    background: 0 0;
	    border: none;
	    border-radius: 2px !important;
	    outline: 0;
	    -webkit-transition: all .2s cubic-bezier(.4, 0, .2, 1), -webkit-box-shadow .2s cubic-bezier(.4, 0, 1, 1);
	    transition: all .2s cubic-bezier(.4, 0, .2, 1), box-shadow .2s cubic-bezier(.4, 0, 1, 1), -webkit-box-shadow .2s cubic-bezier(.4, 0, 1, 1);
	    -webkit-user-drag: none;
	
	    -webkit-box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 3px 1px -2px rgba(0, 0, 0, .2), 0 2px 2px 0 rgba(0, 0, 0, .14), 0 1px 5px 0 rgba(0, 0, 0, .12) !important;
	}
	
	#postsubmit:hover {
	    background-color: rgba(0, 0, 0, .1);
	    opacity: .87;
	    -webkit-box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 2px 4px -1px rgba(0, 0, 0, .2), 0 4px 5px 0 rgba(0, 0, 0, .14), 0 1px 10px 0 rgba(0, 0, 0, .12) !important;
	}
	
	#postsubmit:active {
	    -webkit-box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	    box-shadow: 0 5px 5px -3px rgba(0, 0, 0, .2), 0 8px 10px 1px rgba(0, 0, 0, .14), 0 3px 14px 2px rgba(0, 0, 0, .12) !important;
	}
	
	#postsubmit span,
	#postsubmit em {
	    position: relative;
	    padding: 0 0 0 6px !important;
	    left: 1px;
	}
	
	#postsubmit span:before,
	#postsubmit em:before {
	    content: '';
	    display: inline-block;
	    width: 20px;
	    height: 20px;
	    background: var(--btn);
	    position: relative;
	    top: 5px;
	    left: -10px;
	    -webkit-mask-position: center;
	    -webkit-mask-repeat: no-repeat;
	}
	
	/* region 发布帖子、保存 */
	#postsubmit[name=topicsubmit] span:before,
	#postsubmit[name=editsubmit] span:before,
	#postsubmit[name=replysubmit] span:before {
	    -webkit-mask-image: url("data:image/png;base64,");
	
	}
	
	/* endregion 发布帖子 */
	
	/* region 保存草稿 */
	#postsubmit[onclick^="$('postsave').value = 1;$('postsubmit').click();"] em:before {
	    -webkit-mask-image: url("data:image/png;base64,");
	}
	
	/* endregion 发布帖子 */
	
	
	/* endregion 发表帖子、保存草稿 */
	
	/* region 发帖说明 */
	.mtm.mbm.pnpost > div:last-child {
	    margin-top: 16px !important;
	    background: #eeeeee;
	    border-radius: 5px;
	    padding: 16px !important;
	}
	
