#postlist font[color="#ffff00"]{color:var(--icl)!important}
#postlist font[style="background-color:yellow"],
#postlist font[style="background-color:#ffff00"]{background-color:var(--icl)!important}
#postlist font[color="#191919"],
#postlist font[color="#222222"],#postlist font[color="#222"],
#postlist font[color="#333333"],#postlist font[color="#333"],
#postlist font[style="color:rgb(43, 43, 43)"],
#postlist font[style="color:rgb(51, 51, 51)"],
#postlist font[style="color:rgb(64, 64, 64)"],
#postlist font[style="color:rgb(85, 85, 85)"]{color:var(--t)!important}
#postlist font[style="background-color:#191919"],
#postlist font[style="background-color:#222222"],#postlist font[style="background-color:#222"],
#postlist font[style="background-color:#333333"],#postlist font[style="background-color:#333"],
#postlist font[style="background-color:rgb(43, 43, 43)"],
#postlist font[style="background-color:rgb(51, 51, 51)"],
#postlist font[style="background-color:rgb(64, 64, 64)"],
#postlist font[style="background-color:rgb(85, 85, 85)"]{background-color:var(--t)!important}
/**/

/*14 滚动条*/
	/*14-1 滚动条颜色跟随*/
	html::-webkit-scrollbar-track-piece,html::-webkit-scrollbar-track,textarea::-webkit-scrollbar-track-piece,textarea::-webkit-scrollbar-track{background-color: var(--bg)}
	html::-webkit-scrollbar-thumb:vertical,html::-webkit-scrollbar-thumb,textarea::-webkit-scrollbar-thumb:vertical,textarea::-webkit-scrollbar-thumb{background-color: var(--pri)}
	/*14-2 窄滚动条*/
	html,textarea{scrollbar-color:var(--pri) var(--bg);scrollbar-width:thin}
	html::-webkit-scrollbar,textarea::-webkit-scrollbar{width:8px;height:8px}
	/**/

