@-moz-document regexp("^https?://((education|gist|graphql|guides|docs|raw|resources|status|developer|support|vscode-auth)\\.)?github\\.com/((?!generated_pages/preview).)*$"), domain("githubusercontent.com"), domain("www.githubstatus.com"), domain("stylishthemes.github.io") {
  :root {
    --ghd-bg-custom: /*[[bg-custom]]*/;
    --ghd-bg-img: /*[[bg-choice]]*/;
    --ghd-code-background: #141414;
    --ghd-code-color: #ccc;
    --ghd-body-bg-color: #1c1c1c;
  }
}
@-moz-document regexp("^https?://((education|gist|graphql|guides|docs|raw|resources|status|developer|support|vscode-auth)\\.)?github\\.com/((?!generated_pages/preview).)*$"), domain("githubusercontent.com"), domain("www.githubstatus.com") {
  button {
    color: #b5b5b5;
  }
  /* https://github.com/StylishThemes/GitHub-Dark/issues/968 */
  input, textarea, p {
    color: #c6c6c6;
  }
  svg {
    fill: #c6c6c6;
  }
  svg[fill="none"] {
    fill: none;
  }
  a[href^="https://apps.apple.com"] svg {
    fill: #000;
  }
  .alert {
    background: #182030;
    border-color: #246;
  }
  .CircleBadge, .page-profile .user-status-container {
    background: #181818;
  }
  body[class="page-responsive"] .list-options .list-item > .octicon {
    color: #181818;
  }
  /* begin gist.github.com rules */
  /* gist.github.com: "initial" */
  a {
    background-color: initial;
  }
  /* gist.github.com: "color: #1b1f23" */
  mark {
    color: #dcdcdc;
  }
  /* gist.github.com: "color: #24292e", "#fff" */
  body {
    color: #c6c6c6;
    background-color: var(--ghd-body-bg-color);
  }
  /* gist.github.com: "#0366d6" */
  a {
    color: /*[[base-color]]*/;
  }
  /* gist.github.com: "transparent", "#dfe2e5" */
  .rule, hr {
    background-color: transparent;
    border-bottom-color: #343434;
  }
  /* gist.github.com: "color: #444d56", "#fafbfc", "#d1d5da", "#d1d5da" */
  kbd {
    color: #afafaf;
    background-color: #181818;
    border-color: #404040;
    box-shadow: inset 0 -1px 0 #404040;
  }
  /* gist.github.com: "#fff", "#e1e4e8" */
  .Box {
    background-color: var(--ghd-body-bg-color);
  }
  /* gist.github.com: "#f6f8fa", "#e1e4e8" */
  .Box, .Box-header {
    border-color: #343434;
  }
  .Box-header {
    background-color: #222;
  }
  /* gist.github.com: "#e1e4e8" */
  .Box-body {
    border-bottom-color: #343434;
  }
  /* gist.github.com: "#e1e4e8" */
  .Box-row {
    border-top-color: #343434;
  }
  /* gist.github.com: "#0366d6" */
  .Box-row.Box-row--unread, .Box-row.unread {
    box-shadow: inset 2px 0 0 /*[[base-color]]*/;
  }
  /* gist.github.com: "#0366d6" */
  .Box-row.navigation-focus .Box-row--drag-button {
    color: /*[[base-color]]*/;
  }
  /* gist.github.com: "#fafbfc" */
  .Box-row.navigation-focus.sortable-chosen {
    background-color: #181818;
  }
  /* gist.github.com: "#f6f8fa" */
  .Box-row--focus-gray.navigation-focus,
  .Box-row.navigation-focus.sortable-ghost {
    background-color: #222;
  }
  /* gist.github.com: "#f1f8ff" */
  .Box-row--focus-blue.navigation-focus {
    background-color: #182030;
  }
  /* gist.github.com: "#f6f8fa" */
  .Box-row--hover-gray:hover {
    background-color: #222;
  }
  /* gist.github.com: "#f1f8ff" */
  .Box-row--hover-blue:hover {
    background-color: #182030;
  }
  /* gist.github.com: "color: #24292e", "#0366d6" */
  @media (min-width:768px) {
    .Box-row-link {
      color: #c6c6c6;
    }
    .Box-row-link:hover {
      color: /*[[base-color]]*/;
    }
  }
  /* gist.github.com: "#e1e4e8" */
  .Box-footer {
    border-top-color: #343434;
  }
  /* gist.github.com: "#c8e1ff" */
  .Box--blue {
    border-color: #246;
  }
  /* gist.github.com: "#f1f8ff", "#c8e1ff" */
  .Box--blue .Box-header {
    background-color: #182030;
    border-color: #246;
  }
  /* gist.github.com: "#c8e1ff" */
  .Box--blue .Box-body, .Box--blue .Box-footer, .Box--blue .Box-row {
    border-color: #246;
  }
  /* gist.github.com: "#d73a49" */
  .Box--danger, .Box--danger .Box-body:last-of-type,
  .Box--danger .Box-row:first-of-type {
    border-color: #d82828;
  }
  /* gist.github.com: "#f1f8ff", "#c8e1ff" */
  .Box-header--blue {
    background-color: #182030;
    border-color: #246;
  }
  /* gist.github.com: "#fffbdd" */
  .Box-row--yellow {
    background-color: #261d08;
  }
  /* gist.github.com: "#f1f8ff" */
  .Box-row--blue {
    background-color: #182030;
  }
  /* gist.github.com: "#f6f8fa" */
  .Box-row--gray {
    background-color: #222;
  }
  /* gist.github.com: "#e1e4e8" */
  .breadcrumb-item::after {
    color: #343434;
  }
  /* gist.github.com: "color: #586069" */
  .breadcrumb-item-selected,
  .breadcrumb-item[aria-current]:not([aria-current=false]) {
    color: #afafaf;
  }
  /* gist.github.com: "color: #6a737d" */
  .btn .octicon {
    color: #9b9b9b;
  }
  /* gist.github.com: "rgba(27,31,35,.08)" */
  .btn .Counter {
    background-color: rgba(220, 220, 220, .15);
  }
  /* gist.github.com: "color: #24292e", "#fafbfc", "rgba(27,31,35,.15)", "rgba(27,31,35,.04)" */
  .btn {
    color: #c6c6c6;
    background-color: #181818;
    border-color: rgba(220, 220, 220, .15);
    box-shadow: 0 1px 0 #000, inset 0 1px 0 hsla(0, 0%, 100%, .25);
  }
  /* gist.github.com: "#f3f4f6" */
  .btn.hover, .btn:hover, [open] > .btn {
    background-color: #242424;
  }
  /* gist.github.com: "#edeff2", "box-shadow: inset 0 1px 0 rgba(225,228,232,.2)" */
  .btn.selected, .btn:active, .btn[aria-selected=true] {
    background-color: #343434;
    box-shadow: none;
  }
  /* gist.github.com: "color: #959da5", "#fafbfc", "rgba(27,31,35,.15)" */
  .btn.disabled, .btn:disabled, .btn[aria-disabled=true] {
    color: #767676;
    background-color: #181818;
    border-color: rgba(220, 220, 220, .15);
  }
  /* gist.github.com: "box-shadow: 0 0 0 3px rgba(3,102,214,.3)" */
  .btn.focus, .btn:focus {
    box-shadow: 0 0 0 3px rgba(/*[[base-color-rgb]]*/, .3);
  }
  /* gist.github.com: "color: #fff", "#2ea44f", "rgba(27,31,35,.15)", "box-shadow: 0 1px 0 rgba(27,31,35,.1),inset 0 1px 0 hsla(0,0%,100%,.03)" */
  .btn-primary {
    color: #fff;
    background-color: #373;
    border-color: rgba(220, 220, 220, .15);
    box-shadow: none;
  }
  /* gist.github.com: "#2c974b" */
  .btn-primary.hover, .btn-primary:hover, [open] > .btn-primary {
    background-color: #484;
  }
  /* gist.github.com: "#2a8f47" */
  .btn-primary.selected, .btn-primary:active, .btn-primary[aria-selected=true] {
    background-color: #595;
  }
  /* gist.github.com: "hsla(0,0%,100%,.8)", "#94d3a2", "rgba(27,31,35,.1)", "box-shadow: 0 1px 0 rgba(27,31,35,.1),inset 0 1px 0 hsla(0,0%,100%,.03)" */
  .btn-primary.disabled, .btn-primary:disabled,
  .btn-primary[aria-disabled=true] {
    color: hsla(0, 0%, 100%, .8);
    background-color: #040;
    border-color: rgba(220, 220, 220, .1);
    box-shadow: none;
  }
  /* gist.github.com: "hsla(0,0%,100%,.8)" */
  .btn-primary .octicon {
    color: hsla(0, 0%, 100%, .8);
  }
  /* gist.github.com: "#cb2431" */
  .btn-danger {
    color: #d82828;
  }
  /* gist.github.com: "color: #fff", "#cb2431", "rgba(27,31,35,.15)", "box-shadow: 0 1px 0 rgba(27,31,35,.1),inset 0 1px 0 hsla(0,0%,100%,.03)" */
  .btn-danger:hover, [open] > .btn-danger {
    background-color: #d82828;
    box-shadow: none;
  }
  /* gist.github.com: "color: #fff", "rgba(27,31,35,.15)" */
  .btn-danger.selected, .btn-danger:active, .btn-danger:hover,
  .btn-danger[aria-selected=true], [open] > .btn-danger {
    color: #fff;
    border-color: rgba(220, 220, 220, .15);
  }
  /* gist.github.com: "#fafbfc", "rgba(27,31,35,.15)", "rgba(27,31,35,.04)" */
  .btn-danger.disabled, .btn-danger:disabled, .btn-danger[aria-disabled=true] {
    background-color: #181818;
    border-color: rgba(220, 220, 220, .15);
    box-shadow: 0 1px 0 #000, inset 0 1px 0 hsla(0, 0%, 100%, .25);
  }
  /* gist.github.com: "rgba(203,36,49,.4)" */
  .btn-danger:focus {
    box-shadow: 0 0 0 3px rgba(255, 68, 68, .4);
  }
  /* gist.github.com: "#0366d6" */
  .btn-outline {
    color: /*[[base-color]]*/;
  }
  /* gist.github.com: "color: #fff", "#0366d6", "rgba(27,31,35,.15)", "box-shadow: 0 1px 0 rgba(27,31,35,.1),inset 0 1px 0 hsla(0,0%,100%,.03)" */
